$(function () {
	
	$(".top_banner_wrap .close_wrap").on("click", function () {
		$(this).parents(".top_banner_wrap").stop().animate({
			height: "0"
		}, 50)
	});
	
	$(".siteMap").on("click", function () {
		$(".sitemap_wrap").slideDown(600);
		$("body").addClass("scrollX")
	});
	
	$(".map_close_wrap").on("click", function () {
		$(".sitemap_wrap").slideUp(600);
		$("body").removeClass("scrollX")
	});
	
	$(document).click(function(e) { 
		if($(".scrollX").length) {
			if($(e.target).hasClass("sitemap_wrap")) {
				$(".sitemap_wrap").slideUp(600);
				$("body").removeClass("scrollX");
			}
		}
	});	
	
	$(window).on("scroll", function () {
		var b = $(window).scrollTop(),
			a = $(".content_wrap").position().top;
		b > a ? $(".web_wrap").addClass("sub_menu_active") : $(".web_wrap").removeClass("sub_menu_active")
	});
	
	
	$(".btn_top").on("click", function () {
		$("html,body").animate({
			scrollTop: 0
		}, 400);
		return !1
	});

	
	$('.openTitle').click(function() {
		//$(this).next('.content').slideToggle();
		
		// tr show -> div slideDown
		// div slideUp -> tr hide
		var content = $(this).next(".content");
		if(content.css("display") == "none") {
			content.show().find("div").slideToggle('fast');
		} else {
			content.find("div").slideToggle('fast', function() {
				content.hide();
			});
		}
	})	
	
	/* 모달 fade in out */
//	$(".rightB").click(function() {
//		$(".modal").fadeIn();
//	});
	$(".close_btn").click(function() {
		$(".modal").fadeOut();
	});
	/* //모달 fade in out */
	
	/* 탭 버튼 색상 클래스 적용 */
	$('.tab_bar a:first').addClass('on');
	$('.tab_box > div:first').css('display','block'); 

	$('.tab_bar a').click(function(){
		var name = $(this)[0].name;	
		var index = $(this).parent().index(); 
		if(name.substr(name.length -1 , 1) != "P" && name.substr(name.length -1 , 1) !="F" && name.substr(name.length -1 , 1) != "M"){
			$('.tab_bar a').removeClass('on'); 
			$(this).addClass('on');
			
			$('.tab_box > div').css('display','none'); 
			$('.tab_box > div').eq(index).css('display','block'); 
		}
	});
	/*//탭 버튼 색상 클래스 적용 */
});

function allCheck(){
	//마지막 체크박스 선택 시 전체 체크 혹은 체크 해제
	var chkAgree = $(".vcen2");
	for(i=0;i<chkAgree.length - 1;i++) {
		chkAgree[i].checked = chkAgree[chkAgree.length - 1].checked;
	}
}

function chkAgree() {
	//약관 동의들 다 체크되면 가장 마지막 체크박스 체크됨과 동의 버튼에 파란불 들어오도록 함
	var chkAgree = $(".vcen2");
	var chkAll = 0;
	for(i=0;i<chkAgree.length - 1;i++) {
		if(chkAgree[i].checked) {
			chkAll++;
		}
	}
	if(chkAll == chkAgree.length - 1) {
		chkAgree[chkAgree.length - 1].checked = true;
		$(".btn2").css("background-color", "#3b74db");
	} else {
		chkAgree[chkAgree.length - 1].checked = false;
		$(".btn2").css("background-color", "#484d54");
	}
}
//약관 동의 체크
function chk_agreement(){
	var agree1 = document.getElementById("agreement1");
	var agree2 = document.getElementById("agreement2");
	if(!agree1.checked){
		alert("이용약관에 동의하지 않으셨습니다.\n이용약관에 동의하지 않으실 경우 인터넷 회원로그인을 이용한 서비스 이용이 불가능합니다.");
		return false;
	}
	if(!agree2.checked){
		alert("개인정보 취급방침에 동의하지 않으셨습니다.\n개인정보 취급방침에 동의하지 않으실 경우 인터넷 회원로그인을 이용한 서비스 이용이 불가능합니다.");
		return false;
	}
	return true;
}




