$(function(){
	
	$('.js_menu ul li a.title').click(function(){
		$(this).next("div.minbox").slideToggle(300);
		
		if($(this).is(":visible")) {
			$(this).addClass('on');
			$(this).find('.ico').css('transform','rotate(90deg)');
		} else {
			$(this).removeClass('on');
			$('.ico').css('transform','rotate(0deg)');
		}
	})
	
});



